$(document).ready(function(){  



    $('#formEntrar').submit(function(){

        $.ajax({

            url: 'acao.entrar.php',
            type: 'POST',
            data: $('#formEntrar').serialize(),
                    success: function(data){

                            $('#repostas').html(data);
                            $("#repostas").show('fast');

                    setTimeout(function(){

                            $('#repostas').html(data);
                            $("#repostas").hide('show');

                        }, 
                        
                        6000);
                    }

        });

        return false;

    });

});