
$(document).ready(function(){

jQuery(function () {
  jQuery(window).scroll(function () {
    var height = $('header').outerHeight(true);
    if (jQuery(this).scrollTop() > 0) {
     $("#header-espaco").addClass("header-espaco");
     $("header").addClass("header-fixo");
    } else {
     $("#header-espaco").removeClass("header-espaco");
     $("header").removeClass("header-fixo");
    }
  });
});

 $('#depoimento .owl-carousel').owlCarousel({
      loop:true,
      nav:true,
      padding: 10,
      navText:[
        '<img src="require/img/extra/back.png" alt="Seta esquerda" class="img-fluid" width="45">',
        '<img src="require/img/extra/next.png" alt="Seta direita" class="img-fluid"  width="45">'
        ],
      responsive:{
          0:{
              items: 1
          },

          375:{
              items: 1
          },

          500:{
              items:1
          },

          600:{
              items:1
          },

          1000:{
              items:1
          }
      }
  });

  new WOW().init();

   $("#fone").mask("(99) 9 9999-9999");

  $('#form-copp').submit(function(){
    $.ajax({
       url: 'app/index_f.php',
       type: 'POST',
       data: $('#form-copp').serialize(),
       success: function(data){
            $('#repostas').html(data);
            $("#repostas").show('fast');
          setTimeout(function(){
            $('#repostas').html(data);
            $("#repostas").hide('show');
          }, 10000);
           //$('#repostas').html(data); 3000
       }
    });
    return false;
  });
});
